import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import React, {useState} from "react";
import ExpenseFilter from "./ExpenseFilter";
function Expenses(props) {
  const [selectedFilter,setSelectedFilter] = useState("2022");
  const expenseFilterHandler =(props)=>{
    setSelectedFilter(props);
  };
  
  const filterExpenses =(expense)=>{
    return expense.date.toLocaleString("en-US", { year: "numeric" }) === selectedFilter;
  };
  const filteredExpenses = props.items.filter(filterExpenses);
  return (
    <div>  
      <Card className="expenses">
      <ExpenseFilter filterValue={selectedFilter} onExpenseFilterChange={expenseFilterHandler}/>
      {
        (filterExpenses.length ===0)?<div>No Expenses Found!</div>:filteredExpenses.map((e) => (
          <ExpenseItem key={e.id} title={e.title} date={e.date} price={e.amount} />
        ))
      }
        
      </Card>
    </div>
  );
}
export default Expenses;
