import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import react,{useState} from "react";
import ExpenseFilter from "./ExpenseFilter";
function Expenses(props) {
  const [selectedFilter,setSelectedFilter] = useState("2021");
  const expenseFilterHandler =(props)=>{
    setSelectedFilter(props);
    console.log(selectedFilter);
  };
  return (
    <div>
      <ExpenseFilter onExpenseFilterChange={expenseFilterHandler}/>
      <Card className="expenses">
        {props.items.map((e) => (
          <ExpenseItem title={e.title} date={e.date} price={e.amount} />
        ))}
      </Card>
    </div>
  );
}
export default Expenses;
