import React,{useState} from 'react';
import './ExpenseForm.css';
const ExpenseForm =()=>{
    const [changedTitle,setChangedTitle]=useState("");
    const titleChangeHandler =(e)=>{
        setChangedTitle(e.target.value);
        console.log(changedTitle);
    };
    const [changedAmount,setChangedAmount]=useState("");
    const amountChangeHandler =(e)=>{
        setChangedAmount(e.target.value);
        console.log(changedAmount);
    };

    const [changedDate,setChangedDate]=useState("");
    const dateChangeHandler =(e)=>{
        setChangedAmount(e.target.value);
        console.log(changedDate);
    };
    return (
        <form>
            <div className='new-expense__controls'>
                <div className='new-expense__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' onChange={amountChangeHandler}/>
                </div>
                <div className='new-expense__control'>
                    <label>Date</label>
                    <input type='date' min="2020-01-01" max="2023-12-01" onChange={dateChangeHandler}/>
                </div>                
            </div>
            <div className='new-expense__actions'>
                <button type='submit'>Add Expense</button>
            </div>
        </form>
    );

};

export default ExpenseForm;
